import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

function MainPage() {
  const { city } = useParams(); // URL'den ÅŸehir parametresini alÄ±yoruz
  const [cityInput, setCityInput] = useState(city || ""); // KullanÄ±cÄ± giriÅŸini tutan state
  const [weather, setWeather] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!city) return; // EÄŸer URL'de ÅŸehir parametresi yoksa, herhangi bir iÅŸlem yapma

    const fetchWeather = async () => {
      try {
        const response = await fetch(`https://wttr.in/${city}?format=%C+%t`);
        const data = await response.text();

        if (data.includes("Unknown location")) {
          setWeather(null);
          setError("Åžehir bulunamadÄ±! LÃ¼tfen geÃ§erli bir ÅŸehir adÄ± girin.");
        } else {
          setWeather(data);
          setError(null);
        }
      } catch {
        setWeather(null);
        setError("Bir hata oluÅŸtu, tekrar deneyin.");
      }
    };

    fetchWeather();
  }, [city]); // city parametresi deÄŸiÅŸtiÄŸinde yeniden veri Ã§ek

  const handleInputChange = (e) => {
    setCityInput(e.target.value);
  };

  const handleKeyDown = (e) => {
    if (e.key === "Enter") {
      window.location.href = `/${cityInput.toLowerCase()}`; // Enter ile yÃ¶nlendirme yapÄ±yoruz
    }
  };

  return (
    <div className="flex flex-col items-center justify-center h-screen bg-gray-100">
      <h1 className="text-3xl font-bold mb-4">Hava Durumu UygulamasÄ± ðŸŒ¤</h1>

      {/* Åžehir AdÄ± GiriÅŸi */}
      <input
        type="text"
        placeholder="Åžehir adÄ± girin..."
        value={cityInput}
        onChange={handleInputChange}
        onKeyDown={handleKeyDown}
        className="p-2 border border-gray-400 rounded-md w-64 mb-4"
      />

      {/* Hava Durumunu GÃ¶ster */}
      {city && (
        <div>
          <h2 className="text-2xl mt-4">Hava Durumu: {city}</h2>
          {error ? (
            <p className="text-red-500">{error}</p>
          ) : (
            <p className="text-xl font-semibold">{weather || "YÃ¼kleniyor..."}</p>
          )}
        </div>
      )}
    </div>
  );
}

export default MainPage;
