import React, { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";

const MainPage = () => {
  const [city, setCity] = useState(""); // Kullanıcının girdiği şehir adı
  const [weatherData, setWeatherData] = useState(null);
  const [errorMessage, setErrorMessage] = useState("");
  const [loading, setLoading] = useState(false); // Loading durumu
  const navigate = useNavigate(); // Kullanıcıyı yeni şehire yönlendirmek için

  const getWeatherData = async (cityName) => {
    setLoading(true); // Verileri çekerken loading başlat
    try {
      const response = await fetch(`https://wttr.in/${cityName}?format=%C+%t`);
      
      if (response.status === 404) {
        throw new Error("Şehir bulunamadı. Lütfen geçerli bir şehir adı girin.");
      }

      const data = await response.text();
      setWeatherData(data);
      setErrorMessage("");
    } catch (error) {
      setWeatherData(null);
      setErrorMessage(error.message);
    } finally {
      setLoading(false); // Veriler çekildikten sonra loading'i kapat
    }
  };

  // URL parametrelerinde şehir ismi varsa, onu al
  const { city: urlCity } = useParams();

  useEffect(() => {
    if (urlCity) {
      setCity(urlCity); // URL parametresindeki şehir ismini state'e atıyoruz
      getWeatherData(urlCity);
    }
  }, [urlCity]);

  const handleSubmit = (event) => {
    event.preventDefault();
    if (city) {
      navigate(`/${city}`); // Şehri URL'ye ekleyerek yeni bir rota oluştur
    }
  };

  return (
    <div>
      <h1>Hava Durumu</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Şehir adı girin..."
          value={city}
          onChange={(e) => setCity(e.target.value)}
          className="border p-2"
        />
        <button type="submit" className="ml-2 p-2 bg-blue-500 text-white">
          Ara
        </button>
      </form>

      {/* Yükleniyor animasyonu */}
      {loading && (
        <div className="flex justify-center mt-4">
          <div className="animate-spin rounded-full h-16 w-16 border-t-4 border-blue-500 border-solid"></div>
        </div>
      )}

      {errorMessage && <div className="text-red-500 mt-4">{errorMessage}</div>}
      {weatherData && <div className="text-green-500 mt-4">{weatherData}</div>}
      <h1 className="text-3xl font-bold underline">
      Hello world!
    </h1>
    </div>
  );
};

export default MainPage;
