import { useState } from "react";

function MainPage() {
  const [city, setCity] = useState("");
  const [weather, setWeather] = useState(null);
  const [error, setError] = useState(null);

  const fetchWeather = async () => {
    if (city.trim() === "") return;

    const cityName = city.trim().toLowerCase();

    try {
      const response = await fetch(`https://wttr.in/${cityName}?format=%C+%t`);
      const data = await response.text();

      if (data.includes("Unknown location")) {
        setWeather(null);
        setError("Åžehir bulunamadÄ±! LÃ¼tfen geÃ§erli bir ÅŸehir adÄ± girin.");
      } else {
        setWeather(data);
        setError(null);
      }
    } catch {
      setWeather(null);
      setError("Bir hata oluÅŸtu, tekrar deneyin.");
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === "Enter") {
      fetchWeather();
    }
  };

  return (
    <div className="flex flex-col items-center justify-center h-screen bg-gray-100">
      <h1 className="text-3xl font-bold mb-4">Hava Durumu UygulamasÄ± ðŸŒ¤</h1>

      {/* Åžehir AdÄ± GiriÅŸi */}
      <input
        type="text"
        placeholder="Åžehir adÄ± girin..."
        value={city}
        onChange={(e) => setCity(e.target.value)}
        onKeyDown={handleKeyDown} // Enter tuÅŸu ile arama
        className="p-2 border border-gray-400 rounded-md w-64 mb-4"
      />

      {/* Buton */}
      <button
        onClick={fetchWeather}
        className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600"
      >
        Hava Durumunu GÃ¶ster
      </button>

      {/* Hata MesajÄ± */}
      {error && <p className="text-red-500 mt-4">{error}</p>}

      {/* Hava Durumu Bilgisi */}
      {weather && <p className="text-xl font-semibold mt-4">{weather}</p>}
    </div>
  );
}

export default MainPage;
