import React, { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";

const MainPage = () => {
  const [city, setCity] = useState(""); // Kullanıcının girdiği şehir adı
  const [weatherData, setWeatherData] = useState(null);
  const [errorMessage, setErrorMessage] = useState("");
  const navigate = useNavigate(); // Kullanıcıyı yeni şehire yönlendirmek için

  const getWeatherData = async (cityName) => {
    try {
      const response = await fetch(`https://wttr.in/${cityName}?format=%C`);
      
      if (response.status === 404) {
        throw new Error("Şehir bulunamadı. Lütfen geçerli bir şehir adı girin.");
      }

      const data = await response.text();
      setWeatherData(data);
      setErrorMessage("");
    } catch (error) {
      setWeatherData(null);
      setErrorMessage(error.message);
    }
  };

  // URL parametrelerinde şehir ismi varsa, onu al
  const { city: urlCity } = useParams();

  useEffect(() => {
    if (urlCity) {
      setCity(urlCity); // URL parametresindeki şehir ismini state'e atıyoruz
      getWeatherData(urlCity);
    }
  }, [urlCity]);

  const handleSubmit = (event) => {
    event.preventDefault();
    if (city) {
      navigate(`/${city}`); // Şehri URL'ye ekleyerek yeni bir rota oluştur
    }
  };

  return (
    <div>
      <h1>Hava Durumu</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Şehir adı girin..."
          value={city}
          onChange={(e) => setCity(e.target.value)}
          className="border p-2"
        />
        <button type="submit" className="ml-2 p-2 bg-blue-500 text-white">
          Ara
        </button>
      </form>
      {errorMessage && <div className="text-red-500">{errorMessage}</div>}
      {weatherData && <div className="text-green-500">{weatherData}</div>}
    </div>
  );
};

export default MainPage;
